import './App.scss';
import React, { useState } from 'react';

function App() {

  const [quote, setQuote] = useState('I didn’t fail the test. I just found 100 ways to do it wrong.');
  const [author, setAuthor] = useState('Teddy Roosevelt');
  const [randomNumber, setRandomNumber] = useState(0);

  const generateRandomNumber = () => {
    let randomInteger = Math.floor(3 * Math.random());
    setRandomNumber(randomInteger);
    setRandomNumber();
  }

  return (
    <div className="App">

      <header>
        <p>randomNUmber: {randomNumber}</p>
        <p>"{quote}</p>
        <p>-{author}</p>
        <button onClick={() => generateRandomNumber()}>Change Quote</button>
        <div className="row"><button>twitter</button></div>

      </header>
    </div>
  );
}

export default App;
